import React, { useEffect, useState } from "react";
const Whether = () =>{
    const[name,setname]=useState('')
    const[tempa,settemp]=useState()
    const inputEvent = (event) =>{
        setname(event.target.value)
    }
    useEffect(()=>{
        const Temperature = async () =>{
            const link = await `http://api.openweathermap.org/data/2.5/weather?q=${name}&appid=0709dcfb49669412f3ff11e260d26e06`
            const res = await fetch(link)
            const response = await res.json()
            const temprat = await response.main.temp;
            settemp(temprat)
}
        Temperature()
    },[tempa])
     
    return(
       
        <>
        <div className='whether'>
            <input className='inputCity' type='text' placeholder='enter your cityname' onChange={inputEvent}/>
            <div>
                <p>Your City Name is {name}</p>
                <h2>current Temperature is {tempa}</h2>
            </div> 
        </div>
        </>
    )
}
export default Whether;
