import { LocalConvenienceStoreOutlined } from "@material-ui/icons";
import React, { useEffect, useState } from "react";
// import Axios from 'axios';
const Whether = () =>{
    const[temp,settemp] = useState();
    const[name,setname] = useState();
    useEffect(()=>{
        const whether = async()=>{
        const url =`
        http://api.openweathermap.org/data/2.5/weather?q=${name}&appid=0709dcfb49669412f3ff11e260d26e06`
        const res = await fetch(url)
        const resp = await res.json()
        console.log(resp)
        settemp(resp.main.temp);
        setname(resp.name)

        }
        whether()
    })
   
    return(
       
        <>
        <input type='search' placeholder='enter your location' onChange={((event)=>{
            setname(event.target.value)
        }) } value={name}></input>
        <h2>current location is {name}</h2>
        <h2>current temperature is {temp}</h2>
        </>
    )
}
export default Whether;
